// Debug: Log the incoming data
console.log('Incoming data:', $json);

// Get input numbers - handle both direct and nested data
let num1, num2;

if ($json.body && typeof $json.body === 'object') {
  // Data is in body (common with webhooks)
  num1 = parseFloat($json.body.num1);
  num2 = parseFloat($json.body.num2);
} else {
  // Data is direct
  num1 = parseFloat($json.num1);
  num2 = parseFloat($json.num2);
}

// Validate inputs
if (isNaN(num1) || isNaN(num2)) {
  return [{ 
    json: { 
      error: "Both num1 and num2 must be valid numbers.",
      debug: {
        receivedData: $json,
        parsedNum1: num1,
        parsedNum2: num2,
        num1IsNaN: isNaN(num1),
        num2IsNaN: isNaN(num2)
      }
    } 
  }];
}

// Calculate the sum
const result = num1 + num2;

// Return the result
return [{
  json: {
    result: result,
    calculation: `${num1} + ${num2} = ${result}`,
    inputs: {
      num1: num1,
      num2: num2
    },
    timestamp: new Date().toISOString()
  }
}];
